import React, { Component } from "react";
import "./Mentor.css";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

class Mentor extends Component {
  constructor(props) {
    super(props);
    this.state = {
        redirect: false
    };
  }

  handleRedirectChange(){
      this.setState({ redirect: true});
  }

  renderRedirect(){
      if(this.state.redirect){
          return <Redirect to='../Tutors/Tutor' />
      }
  }
  render() {
    return (
      <div className="mentor">
        <div className="mentor-text">
          <h1>Connect with your mentor</h1>
          <p>
            Have any questions about your course? Approach your personal tutor
            and get your doubts clarified. Your question will be emailed to your
            respective tutor's email
          </p>
          <h2 onClick={this.handleRedirectChange}>Connect now</h2>
        </div>
      </div>
    );
  }
}

export default Mentor;

