import React, { Component } from "react";
import "./Mentor.css";
import { BrowserRouter as Router, Link, Route, Switch } from "react-router-dom";

class Mentor extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false
    };
    this.handleRedirectChange = this.handleRedirectChange.bind(this);
  }

  render() {
    return (
      <div className="mentor">
        <div className="mentor-text">
          <h1>Connect with your mentor</h1>
          <p>
            Have any questions about your course? Approach your personal tutor
            and get your doubts clarified. Your question will be emailed to your
            respective tutor's email
          </p>
          {/* <h2>Connect now</h2> */}
          <Router>
            <Link to="../Tutors/Tutors">Connect Now</Link>
            <Switch>
              <Route path="../Tutors/Tutors" component={Tutors} />
            </Switch>
          </Router>
        </div>
      </div>
    );
  }
}

export default Mentor;
